-- Create function for semantic search
CREATE OR REPLACE FUNCTION search_vividwalls(
    query_embedding vector(384),
    match_count INTEGER DEFAULT 10,
    filter_source_type TEXT DEFAULT NULL
)
RETURNS TABLE (
    knowledge_id INTEGER,
    title TEXT,
    content TEXT,
    chunk_text TEXT,
    source_type TEXT,
    similarity FLOAT
)
LANGUAGE plpgsql
AS $$
BEGIN
    RETURN QUERY
    SELECT 
        k.id AS knowledge_id,
        k.title,
        k.content,
        e.chunk_text,
        k.source_type,
        1 - (e.embedding <=> query_embedding) AS similarity
    FROM vividwalls_embeddings e
    JOIN vividwalls_knowledge k ON k.id = e.knowledge_id
    WHERE (filter_source_type IS NULL OR k.source_type = filter_source_type)
    ORDER BY e.embedding <=> query_embedding
    LIMIT match_count;
END;
$$;

-- Create view for statistics
CREATE OR REPLACE VIEW vividwalls_stats AS
SELECT 
    'Total Records' as metric,
    COUNT(*)::TEXT as value
FROM vividwalls_knowledge
UNION ALL
SELECT 
    'Total Embeddings' as metric,
    COUNT(*)::TEXT as value
FROM vividwalls_embeddings
UNION ALL
SELECT 
    'Source: ' || source_type as metric,
    COUNT(*)::TEXT as value
FROM vividwalls_knowledge
GROUP BY source_type;

