// Daily Morning Review Automation
const performMorningReview = async () => {
  const reviews = [];

  // 1. Check overnight sales
  const overnightOrders = await $tools.shopifyMCP.get_orders({
    created_at_min: 'yesterday_evening',
    status: 'any'
  });

  // 2. Review Facebook Ads
  const fbMetrics = await $tools.facebookMCP.get_adaccount_insights({
    date_preset: 'yesterday',
    fields: ['spend', 'revenue', 'roas']
  });

  // 3. Auto-optimize if needed
  if (fbMetrics.roas < 2.5) {
    await $tools.facebookMCP.bulk_update_campaigns({
      filter: { daily_budget: '>100' },
      updates: { daily_budget_multiplier: 0.8 }
    });
    reviews.push('Reduced budget for underperforming campaigns');
  }

  return {
    timestamp: new Date().toISOString(),
    reviews: reviews,
    metrics: {
      overnight_revenue: overnightOrders.total,
      facebook_roas: fbMetrics.roas
    }
  };
};

return await performMorningReview();

// Performance Crisis Detection
const detectCrisis = (metrics) => {
  const triggers = [];

  // Check ROAS threshold
  if (metrics.daily_roas < 2.0) {
    triggers.push({
      type: 'ROAS_CRISIS',
      severity: 'high',
      action: 'pause_underperformers'
    });
  }

  // Check CAC threshold
  if (metrics.cac > 50) {
    triggers.push({
      type: 'CAC_CRISIS',
      severity: 'medium',
      action: 'optimize_targeting'
    });
  }

  // Check conversion rate
  if (metrics.conversion_rate < 0.02) {
    triggers.push({
      type: 'CONVERSION_CRISIS',
      severity: 'high',
      action: 'review_funnel'
    });
  }

  return {
    crisis_detected: triggers.length > 0,
    triggers: triggers,
    recommended_actions: triggers.map(t => t.action)
  };
};

return detectCrisis($json.metrics);

// Generate Executive Summary
const generateExecutiveSummary = (data) => {
  const summary = {
    date: new Date().toISOString().split('T')[0],
    kpis: {
      revenue: data.revenue || 0,
      spend: data.spend || 0,
      roas: (data.revenue / data.spend).toFixed(2),
      new_customers: data.new_customers || 0,
      conversion_rate: data.conversion_rate || 0
    },
    performance: {
      vs_yesterday: calculateChange(data.revenue, data.yesterday_revenue),
      vs_last_week: calculateChange(data.revenue, data.last_week_revenue),
      vs_target: calculateChange(data.revenue, data.target_revenue)
    },
    top_channels: rankChannels(data.channel_performance),
    recommendations: generateRecommendations(data),
    alerts: identifyAlerts(data)
  };

  return summary;
};

const calculateChange = (current, previous) => {
  if (!previous) return 0;
  return ((current - previous) / previous * 100).toFixed(1);
};

const rankChannels = (channels) => {
  return Object.entries(channels || {})
    .sort(([,a], [,b]) => b.revenue - a.revenue)
    .slice(0, 3)
    .map(([name, data]) => ({
      name,
      revenue: data.revenue,
      roas: data.roas
    }));
};

const generateRecommendations = (data) => {
  const recs = [];

  if (data.roas > 4) {
    recs.push('Scale high-performing campaigns by 25%');
  }

  if (data.cac > 45) {
    recs.push('Optimize targeting to reduce CAC');
  }

  if (data.inventory_low) {
    recs.push('Restock top-selling products urgently');
  }

  return recs;
};

const identifyAlerts = (data) => {
  const alerts = [];

  if (data.roas < 2.5) {
    alerts.push({ type: 'warning', message: 'ROAS below threshold' });
  }

  if (data.budget_utilization > 0.95) {
    alerts.push({ type: 'info', message: 'Budget nearly exhausted' });
  }

  return alerts;
};

return generateExecutiveSummary($json);

