// Output Parser for structured responses
const parseOutput = (response) => {
  try {
    // Extract structured data from AI response
    const parsed = {
      action: extractAction(response),
      data: extractData(response),
      next_steps: extractNextSteps(response),
      notifications: extractNotifications(response)
    };

    return parsed;
  } catch (error) {
    return {
      action: 'review',
      data: { raw_response: response },
      error: error.message
    };
  }
};

const extractAction = (text) => {
  const actionPattern = /ACTION:\s*([^\n]+)/i;
  const match = text.match(actionPattern);
  return match ? match[1].trim() : 'none';
};

const extractData = (text) => {
  const dataPattern = /DATA:\s*({[^}]+})/i;
  const match = text.match(dataPattern);
  return match ? JSON.parse(match[1]) : {};
};

const extractNextSteps = (text) => {
  const stepsPattern = /NEXT STEPS:\s*([^\n]+)/gi;
  const matches = text.matchAll(stepsPattern);
  return Array.from(matches).map(m => m[1].trim());
};

const extractNotifications = (text) => {
  const notifyPattern = /NOTIFY:\s*([^\n]+)/gi;
  const matches = text.matchAll(notifyPattern);
  return Array.from(matches).map(m => m[1].trim());
};

return parseOutput($input.response);

