// Extract blog post details for Telegram approval message
const agentOutput = $('Content Marketing Agent').first().json;

// Parse content details from agent output
const contentDetails = {
  post_id: agentOutput.post_id || `post_${Date.now()}`,
  post_title: agentOutput.post_title || 'Blog Post',
  post_type: agentOutput.post_type || 'blog_post',
  word_count: agentOutput.word_count || 'TBD',
  reading_time: agentOutput.reading_time || 'TBD',
  seo_score: agentOutput.seo_score || 'TBD',
  seo_details: agentOutput.seo_details || {},
  content_structure: agentOutput.content_structure || {},
  html_preview: agentOutput.html_preview || '<p>Preview not available</p>',
  approval_deadline: '24 hours'
};

// Format Telegram message with HTML preview
const telegramMessage = `üìù VividWalls Blog Post Approval Required

Post: ${contentDetails.post_title}
Type: ${contentDetails.post_type}
Reading Time: ${contentDetails.reading_time} (${contentDetails.word_count} words)
SEO Score: ${contentDetails.seo_score}

üéØ SEO OPTIMIZATION
‚Ä¢ Primary Keyword: "${contentDetails.seo_details.primary_keyword || 'TBD'}" (${contentDetails.seo_details.search_volume || 'TBD'} searches/month)
‚Ä¢ Expected Traffic: ${contentDetails.seo_details.expected_traffic || 'TBD'} monthly visitors
‚Ä¢ Readability: ${contentDetails.seo_details.readability || 'Good'}
‚Ä¢ Internal Links: ${contentDetails.seo_details.internal_links || 'TBD'} relevant links
‚Ä¢ Meta Description: ${contentDetails.seo_details.meta_optimized || 'Optimized'}

üìö CONTENT STRUCTURE
${Object.entries(contentDetails.content_structure).map(([key, value]) => `‚Ä¢ ${key}: ${value}`).join('\n')}

üåê BLOG POST PREVIEW:
${contentDetails.html_preview}

Approve? Reply:
‚úÖ APPROVE - Publish blog post
üìÖ SCHEDULE - Schedule for later
‚ùå REJECT - Don't publish
‚úèÔ∏è EDIT - Request changes
üìß EMAIL-TOO - Also create email newsletter

Post ID: ${contentDetails.post_id}`;

return {
  json: {
    telegram_message: telegramMessage,
    post_id: contentDetails.post_id,
    content_details: contentDetails,
    approval_type: 'blog_post'
  }
};

// Execute approved content publication using WordPress MCP tools
const approvalData = $('Content Approval Webhook').first().json;
const contentDetails = $('Format Content Approval').first().json.content_details;

// Prepare publication commands based on approval type
const publicationPlan = {
  post_id: contentDetails.post_id,
  approval_type: approvalData.approval_type || 'publish',
  publication_timestamp: new Date().toISOString(),
  publish_immediately: approvalData.approval_type === 'approved',
  schedule_publication: approvalData.approval_type === 'scheduled',
  create_newsletter: approvalData.create_newsletter || false,
  scheduled_date: approvalData.scheduled_date || null
};

// Log publication for monitoring
console.log('Publishing approved content:', publicationPlan.post_id);
console.log('Publication type:', publicationPlan.approval_type);

return {
  json: {
    publish_content: true,
    publication_plan: publicationPlan,
    content_details: contentDetails,
    approval_received: approvalData
  }
};
