// Extract campaign details for Telegram approval message
const agentOutput = $('Marketing Campaign Agent').first().json;

// Parse campaign details from agent output
const campaignDetails = {
  campaign_id: agentOutput.campaign_id || `campaign_${Date.now()}`,
  campaign_name: agentOutput.campaign_name || 'Marketing Campaign',
  total_cost: agentOutput.total_cost || 'TBD',
  pinterest_details: agentOutput.pinterest_details || {},
  email_details: agentOutput.email_details || {},
  email_html_preview: agentOutput.email_html_preview || '',
  approval_deadline: '24 hours'
};

// Format Telegram message
const telegramMessage = `üé® VividWalls Campaign Approval Required

Campaign: ${campaignDetails.campaign_name}
Total Cost: ${campaignDetails.total_cost}
Deadline: ${campaignDetails.approval_deadline}

üìå PINTEREST ADS
‚Ä¢ Budget: ${campaignDetails.pinterest_details.daily_budget || 'TBD'}/day √ó ${campaignDetails.pinterest_details.duration || 'TBD'} days
‚Ä¢ Targeting: ${campaignDetails.pinterest_details.targeting || 'Art collectors'}
‚Ä¢ Creative: "${campaignDetails.pinterest_details.ad_title || 'Campaign Creative'}"
‚Ä¢ Expected: ${campaignDetails.pinterest_details.expected_metrics || 'Performance TBD'}

üìß EMAIL CAMPAIGN
‚Ä¢ Audience: ${campaignDetails.email_details.audience || 'Subscribers'}
‚Ä¢ Cost: ${campaignDetails.email_details.cost || 'TBD'}
‚Ä¢ Subject: "${campaignDetails.email_details.subject || 'Email Subject'}"
‚Ä¢ Expected: ${campaignDetails.email_details.expected_metrics || 'Performance TBD'}

üì± EMAIL PREVIEW:
${campaignDetails.email_html_preview}

Approve? Reply:
‚úÖ APPROVE - Execute both campaigns
üìå APPROVE-ADS - Pinterest ads only  
üìß APPROVE-EMAIL - Email campaign only
‚ùå REJECT - Cancel campaigns
üí¨ MODIFY - Request changes

Campaign ID: ${campaignDetails.campaign_id}`;

return {
  json: {
    telegram_message: telegramMessage,
    campaign_id: campaignDetails.campaign_id,
    campaign_details: campaignDetails,
    approval_type: 'marketing_campaign'
  }
};

// Execute approved campaign using MCP tools
const approvalData = $('Approval Response Webhook').first().json;
const campaignDetails = $('Format Approval Message').first().json.campaign_details;

// Prepare execution commands based on approval type
const executionPlan = {
  campaign_id: campaignDetails.campaign_id,
  approved_components: approvalData.approved_components || ['pinterest', 'email'],
  execution_timestamp: new Date().toISOString(),
  pinterest_execution: approvalData.approved_components.includes('pinterest'),
  email_execution: approvalData.approved_components.includes('email')
};

// Log execution for monitoring
console.log('Executing approved campaign:', executionPlan.campaign_id);
console.log('Components to execute:', executionPlan.approved_components);

return {
  json: {
    execute_campaign: true,
    execution_plan: executionPlan,
    campaign_details: campaignDetails,
    approval_received: approvalData
  }
};
